import React,{Component} from 'react';
import { Map as LeafletMap, TileLayer, CircleMarker} from 'react-leaflet';

const position = [-15.89929, -47.49445]
class Map extends Component{
  state = {allDatas : {}}


  componentDidUpdate(){
    fetch('http://104.248.25.93:9000/average/'+this.props.observation.yr+'/'+this.props.observation.mo+'/'+this.props.observation.type)
    .then(response => response.json())
    .then(json => this.setState({allDatas:json.data}))
    .catch( error => alert(error.message));
}

  render(){
      return(
        <LeafletMap style={{height:'100%'}}
        center={position}
        zoom={4}
        maxZoom={10}
        attributionControl={true}
        zoomControl={true}
        doubleClickZoom={true}
        scrollWheelZoom={true}
        dragging={true}
        animate={true}
        easeLinearity={0.35}
      >
        <TileLayer
          url='https://{s}.tile.opentopomap.org/{z}/{x}/{y}.png'
        />

        {this.state.allDatas.map(data=>(
          <CircleMarker center={[data.lat,data.lon]}></CircleMarker>
        ))}

      </LeafletMap>
      )
  }
}
export default Map
